import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [userDetails, setUserDetails] = useState({
    category: '',
    question: '',
    answer: '',
    difficulty: ''
  });

  useEffect( () => {
    getAPI();
    console.log("Page Loaded");
  }, [])

  const getAPI = async() => {
    const res = await axios.get('https://opentdb.com/api.php?amount=10&category=18&difficulty=hard&type=multiple')
    console.log(res.data);  //res.data.value

    setUserDetails({
      category: res.data.results[1].category,
      question: res.data.results[1].question,
      answer: res.data.results[1].incorrect_answers,
      difficulty: res.data.results[1].difficulty
    });
  }


  return (
    <div className="App">
      <h1>Hello From React</h1>
      <h4>Category: {userDetails.category}</h4>
      <h4>Question: {userDetails.question}</h4>
      <h4>Answer: {userDetails.answer}</h4>
      <h4>Difficulty: {userDetails.difficulty}</h4>
    </div>
  );
}

export default App;
