import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';

function Question() {
  const [questions, setQuestion] = useState([]);
    // category: '',
    // question: '',
    // answer: '',
    // difficulty: ''
  

  useEffect( () => {
    getAPI();
    console.log("Page Loaded");
  }, [])

  const getAPI = async() => {
    const res = await axios.get('https://opentdb.com/api.php?amount=10&category=18&difficulty=hard&type=multiple')
    console.log(res.data);  //res.data.value

    setQuestion(res.data.results
    //   category: res.data.results.category,
    //   question: res.data.results.question,
    //   answer: res.data.results.incorrect_answers,
    //   difficulty: res.data.results.difficulty
    );
  }

  return (
    <div className='question_container'>
        
            <div>
                <h2>
                    {res.questions[0].question}
                </h2>
            </div>
            <div>
                <button>{res.questions[0].correct_answer}</button>
                <button>{res.questions[0].incorrect_answer[0]}</button>
                <button>{res.questions[0].incorrect_answer[1]}</button>
                <button>{res.questions[0].incorrect_answer[2]}</button>
            </div>
        
    </div>

  )
    
}

export default Question;
