import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';

function Question() {
  const [questions, setQuestion] = useState({
    category: '',
    question: '',
    answer: '',
    difficulty: ''
  });

  useEffect( () => {
    getAPI();
    console.log("Page Loaded");
  }, [])

  const getAPI = async() => {
    const res = await axios.get('https://opentdb.com/api.php?amount=10&category=18&difficulty=hard&type=multiple')
    console.log(res.data);  //res.data.value

    setQuestion({
      category: res.data.results.category,
      question: res.data.results.question,
      answer: res.data.results.incorrect_answers,
      difficulty: res.data.results.difficulty
    });
  }

  return (
    <div className='question_container'>
      <div>
          <h2>
              this is where question states
          </h2>
      </div>
      <div>
          <button>answer 1</button>
          <button>answer 2</button>
          <button>answer 3</button>
          <button>answer 4</button>
      </div>
      
    </div>
  );
}

export default Question;
